                <th>Status</th>
                <td>{{ order.status ? 'Yes' : 'No' }}</td>
            </tr>
            <tr>
                <th>ClientEmail</th>
                <td>{{ order.clientEmail }}</td>
            </tr>
            <tr>
                <th>DeliveryDt</th>
                <td>{{ order.deliveryDt ? order.deliveryDt|date('Y-m-d H:i:s') : '' }}</td>
            </tr>
            <tr>
                <th>CreatedAt</th>
                <td>{{ order.createdAt ? order.createdAt|date('Y-m-d H:i:s') : '' }}</td>
            </tr>
